import React from "react";
import {PropTypes} from "prop-types";
import Style from '../../assets/style/dataTable.scss'

const DataTable = (props) => {
    const data = props.data

    function renderTableHeader() {
        const header = Object.keys(data[0])
        return header.map((key, index) => {
            return <th key={index}>{key.toUpperCase()}</th>
        })
    };

    function renderTags(tags) {
        const dataTags = Object.values(tags);
        return <td key={index}><span className={tag.class}>{tag.name}</span></td>
    }

    function renderRow(row) {
        const dataRow = Object.keys(row);
        return dataRow.map((key, index) => {
            console.log('key', key);
            if (key === 'tags') return renderTags(row[key]);
            return <td key={index}><span>{row[key]}</span></td>
        })
    }

    function renderTableData() {
        return data.map((row, index) => {
            return <tr key={row.id}>{renderRow(row)}</tr>
        })
    }

    return (
        <table className="data-table">
            <tbody>
            <tr>{renderTableHeader()}</tr>
            {renderTableData()}
            </tbody>
        </table>
    );
}

DataTable.propTypes = {
    data: PropTypes.array
};
export default DataTable
